import React, { Component } from 'react'
import {NavLink} from 'react-router-dom'
import ABCLogo from '../Images/ABCLogo.png'

class Navigation extends Component {

    constructor(){
        super()
    
        /** State variables: 
         * - menu controls the navbar when the screen size is below  bootstrap-sm attribute.
         */
        this.state = {
          menu: false,
        }
    }

    /**
   * Handles all navbar hide/show effects
   * @param  {Javascript Event} event the event associated witht the listener
   * @return none
   */
    toggleMenu(event){
        const name = event.currentTarget.name
        this.setState({
        [name]: !this.state[name]
        })
    }



    render() {
        return (
            <nav className="navbar navbar-expand-sm navbar-light bg-light">
               <img src={ABCLogo} width="100" height="70" alt=""/>
                <button className="navbar-toggler" type="button" onClick={this.toggleMenu} data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon" name="menu" onClick={this.toggleMenu}></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNavDropdown">
                    <ul className="navbar-nav">
                    <li className="nav-item active">
                        <NavLink className='nav-link' to='/home'>Home</NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className='nav-link' to='/employee'>Employees</NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className='nav-link' to='/'>Logout</NavLink>
                    </li>
                    </ul>
                </div>
            </nav>
        )
    }  
}

export default Navigation
